mapper.select:
select *
from user
where username = #{username}
  and userAge = #{userAge}

select *
from user
where username = #{username}
  and userAge = #{userAge}
  and userSex = #{userSex}
  and flag = 0

mapper.update:
update scan.user
set scan.user.flag= 1
where scan.user.xuhao = #{preId}

update scan.user
set scan.user.xuhao= 0
where flag = 1;

update scan.user
set scan.user.xuhao= #{lastUserId},
    flag=0
where username = #{username}
  and userAge = #{userAge}
  and userSex = #{userSex};
